export const getRequest = (url , type, data) => {
    fetch(url, {
        method: 'GET',
        headers: {
            "Content-type": "application/json; charset=UTF-8"
        }
    })
}

export const postRequest = (url, type, data) => {
    fetch(url, {
        method: "POST",
        body: JSON.stringify(data),
        headers: {
            "Content-type": "application/json; charset=UTF-8"
        }
    })
}


export const deleteRequest = (url, id, data) => {
    fetch(url + id, {
        method: "DELETe",
        // body: JSON.stringify(data),
        headers: {
            "Content-type": "application/json; charset=UTF-8"
        }
    })
}
