import React, { Component } from 'react'
import Jwt from 'jwt-decode';


const LoginWrapper = {
    width: '400px',
    padding: ' 20px 10px',
    boxSizing: "border-box",
    border: '1px solid #ccc',
    boxShadow: '2px 1px 3px #ccc',
    borderRadius: '3px',
    margin: 'auto'
};

const inputStyle = {
    width: '100%',
    height: '40px',
    lineHeight: '40px',
    borderRadius: '3px',
    border: '1px solid #ccc',
    padding: '5px',
    fontSize: '18px',
    fontWeight: '600',
    boxSizing: 'border-box'
}

const loginBtn = {
    padding: '10px 20px',
    margin: 'auto',
    fontSize: '17px',
    textAlign: 'center'
}



const divStyle = {
    margin: '40px',
    border: '5px solid pink'
};

class login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            email: 'send4saroj@gmail.com',
            password: 'saroj@852131'
        }
        const token = localStorage.getItem("token") ? localStorage.getItem("token") : null;
        const jwtData = Jwt(token)
        console.log(jwtData)
    }


    inputChange = e => {
        this.setState({
            [e.target.name]: e.target.value
        }, () => console.log(this.state))
    }


    onSubmit = () => {
        let data = {
            email: this.state.email,
            password: this.state.password
        }
        try {
            fetch('http://localhost:5000/api/user/login', {
                method: 'POST',
                body: JSON.stringify(data),
                headers: {
                    "Content-type": "application/json; charset=UTF-8"
                }
            })
                .then(response => response.json())
                .then(json => {
                    localStorage.setItem("token", json.token)
                })
        }
        catch (error) {
            console.log(error.message)

        }

    }

    // componentDidMount = () => {
    //     console.log("hey")
    //     fetch("http://localhost:5000/api/post",{ method: "get"})
    //     .then(response => response.json())
    //     .then(json => console.log(json))

    // }


  

    render() {
        return (
            <div>
                <div style={LoginWrapper}>
                    <h1>Login</h1>
                    <div style={{ textAlign: 'left' }}>
                        <label style={{ fontSize: '20px', textAlign: 'left', marginBottom: '7px', display: 'block', fontWeight: '300' }}>Email</label>
                        <input style={inputStyle} type="text" name="email" value={this.state.email} onChange={this.inputChange} />
                    </div>
                    <br></br>
                    <div style={{ textAlign: 'left' }}>
                        <label style={{ fontSize: '20px', textAlign: 'left', marginBottom: '7px', display: 'block', fontWeight: '300' }}>Password</label>
                        <input style={inputStyle} type="text" name="password" value={this.state.password} onChange={this.inputChange} />
                    </div>
                    <br></br>
                    <div style={{ textAlign: 'center' }}>
                        <button style={loginBtn} onClick={this.onSubmit}>Login</button>
                    </div>
                </div>

            </div>
        )
    }
}
export default login
