import React, { Component } from 'react'

const LoginWrapper = {
    width: '400px',
    padding: ' 20px 10px',
    boxSizing: "border-box",
    border: '1px solid #ccc',
    boxShadow: '2px 1px 3px #ccc',
    borderRadius: '3px',
    margin: 'auto'
};

const inputStyle = {
    width: '100%',
    height: '40px',
    lineHeight: '40px',
    borderRadius: '3px',
    border: '1px solid #ccc',
    padding: '5px',
    fontSize: '18px',
    fontWeight: '600',
    boxSizing: 'border-box'
}

const loginBtn = {
    padding: '10px 20px',
    margin: 'auto',
    fontSize: '17px',
    textAlign: 'center'
}



const divStyle = {
    margin: '40px',
    border: '5px solid pink'
};

class login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            password: ''
        }
    }


    inputChange = e => {
        this.setState({
            [e.target.name] : e.target.value
        }, () => console.log(this.state))
    }


    onSubmit = () => {
        let data = {
            email: this.state.email,
            password: this.state.password
        }

        try{
            const response=  fetch('/api/user/login', {
                method: 'POST', 
                body: JSON.stringify(data), 
                headers: {
                  'Content-Type': 'application/json'
                }
              });
              const json =  response.json();
              console.log('Success:', JSON.stringify(json));
        }
         catch (error) {
          console.error('Error:', error);
        


    }
}


    render() {
        return (
            <div>
                <div style={LoginWrapper}>
                    <h1>Login</h1>
                    <div style={{ textAlign: 'left' }}>
                        <label style={{ fontSize: '20px', textAlign: 'left', marginBottom: '7px', display: 'block', fontWeight: '300' }}>Email</label>
                        <input style={inputStyle} type="text" name="email" value={this.state.email} onChange={this.inputChange} />
                    </div>
                    <br></br>
                    <div style={{ textAlign: 'left' }}>
                        <label style={{ fontSize: '20px', textAlign: 'left', marginBottom: '7px', display: 'block', fontWeight: '300' }}>Password</label>
                        <input style={inputStyle} type="text" name="password" value={this.state.password} onChange={this.inputChange} />
                    </div>
                    <br></br>
                    <div style={{ textAlign: 'center' }}>
                        <button style={loginBtn} onClick={this.onSubmit}>Login</button>
                    </div>
                </div>

            </div>
        )
    }
}
export default login
