import React, { Component } from 'react'
import { connect } from 'react-redux';
import { newPosts } from '../action/postAction';
import styled from 'styled-components';
import {loginRequest, signUpRequest} from '../action/authAction';


const Main = styled.div`
    width: 95%;
    display: flex;
    justify-content: space-between;
    margin: auto;
`;

const inputStyle = {
    width: '100%',
    height: '40px',
    lineHeight: '40px',
    borderRadius: '3px',
    border: '1px solid #ccc',
    padding: '5px',
    fontSize: '18px',
    fontWeight: '600',
    boxSizing: 'border-box'
}

const SideBar = styled.div`
    width: 100px;
    height: 100%;
    background: #a1a1a1;
    border-right: 1px solid #ccc;
    .row{
        width: 100%;
        height: 50px;
        display: flex;
        align-items: center;
        justify-content: center;
        padding:0px 10px;
        border-bottom: 1px solid #ccc;
        box-sizing: border-box;
        cursor: pointer;
        font-size: 15px;
        font-weight: 400;
        text-transform: uppercase;
        i{
            color: red;
            font-size: 30px;
            margin-right: 10px;
        }
    }

`;



const RightSide = styled.div`
        width: calc(100% - 120px);
        height: 500px;
        overflow: scroll;
        border: 1px solid #ccc;
        padding: 10px;
        box-sizing: border-box;
        text-align: left;

        input{
            width: 100%;
            height: 50px;
            line-height: 50px;  
            padding: 10px;
            box-sizing: border-box;
        
        }

        label{
            margin-bottom: 10px;
            font-size: 15px;
            display: block;
            font-weight: 500;
        }

        .row{
            width: 100%;
            margin-bottom: 10px;
        }

`;



class admin extends Component {
    constructor(props) {
        super(props);
        this.state = {
            title: "",
            body: "",
            image: "",
            tab: "user"

        }
    }

    onChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    // New post submit 
    postSubmit = () => {
        this.props.newPosts(this.state.title, this.state.image, this.state.body)
        this.setState({
            title: "",
            image: '',
            body: ""
        });
        alert("POST SUCCESS FULL");
    }


    // create new user 

    signUp = () => {
        let data = {
            firstName: this.state.fullName,
            middleName: "",
            lastName: "yadav",
            email: this.state.newEmail,
            password: this.state.newPassword,
            profilePic: this.state.profilePic,
            dob: this.state.dob,
        }
        this.props.signUpRequest(data)
    }





    changeTab = (key) => {
        this.setState({
            tab: key
        })
    }


    render() {

        if (!localStorage.getItem("token")) {
            return <h1>Please Login</h1>
        }
        return (

            <div>
                <Main>
                    <SideBar>
                        <div className="row" onClick={() => this.changeTab("post")}>
                            <i class="fab fa-blogger"></i> Post
                        </div>
                        <div className="row"  onClick={() => this.changeTab("user")}>
                            <i class="fas fa-users"></i>User
                        </div>

                    </SideBar>

                    <RightSide>
                        <h1 style={{ textAlign: 'left', borderBottom: '1px solid #ccc', paddingBottom: "10px", marginTop: '0px' }}>
                            {this.state.tab}
                        </h1>

                       {
                           this.state.tab === "post" &&
                           <div class="post">
                           <div className="row">
                               <label>Post title</label>
                               <input type="text" name="title" value={this.state.title} onChange={this.onChange} />
                           </div>

                           <div className="row">
                               <label>Post Body</label>
                               <textarea type="text" name="body" value={this.state.body} onChange={this.onChange} style={{ height: '100px', width: '100%' }} />
                           </div>


                           <div className="row">
                               <label>Post images</label>
                               <input type="file" name="image" value={this.state.image} onChange={this.onChange} />
                           </div>
                           <button onClick={this.postSubmit}>Submit Your Post</button>
                       </div>
                       
                       }




                       {/* user    */}


                       <div class="user">
                            {this.state.addNewUSer &&  <div>
                            <div style={{ textAlign: 'left' }}>
                                <label style={{ fontSize: '20px', textAlign: 'left', marginBottom: '7px', display: 'block', fontWeight: '300' }}>Full Name</label>
                                <input style={inputStyle} type="text" name="fullName" value={this.state.fullName} onChange={this.inputChange} />
                            </div>
                            <div style={{ textAlign: 'left' }}>
                                <label style={{ fontSize: '20px', textAlign: 'left', marginBottom: '7px', display: 'block', fontWeight: '300' }}>profilePic</label>
                                <input style={inputStyle} type="file" name="profilePic" value={this.state.profilePic} onChange={this.inputChange} />
                            </div>

                            <div style={{ textAlign: 'left' }}>
                                <label style={{ fontSize: '20px', textAlign: 'left', marginBottom: '7px', display: 'block', fontWeight: '300' }}>Email</label>
                                <input style={inputStyle} type="text" name="newEmail" value={this.state.newEmail} onChange={this.inputChange} />
                            </div>
                            <br></br>
                            <div style={{ textAlign: 'left' }}>
                                <label style={{ fontSize: '20px', textAlign: 'left', marginBottom: '7px', display: 'block', fontWeight: '300' }}>Password</label>
                                <input style={inputStyle} type="text" name="newPassword" value={this.state.newPassword} onChange={this.inputChange} />
                            </div>
                            <br></br>
                            <div style={{ textAlign: 'left' }}>
                                <label style={{ fontSize: '20px', textAlign: 'left', marginBottom: '7px', display: 'block', fontWeight: '300' }}>Date of birth</label>
                                <input style={inputStyle} type="date" name="dob" value={this.state.dob} onChange={this.inputChange} />
                            </div>
                            <br></br>

                            <div style={{ textAlign: 'center' }}>
                                <button onClick={this.signUp}>signUp</button>
                            </div>
                        </div>}


                        <div class="user_card">
                            <h2>saroj Kumar</h2>
                            

                        </div>
                       </div>

                    </RightSide>
                </Main>
            </div>
        )
    }
}

export default connect(null, { newPosts , signUpRequest})(admin);
