import {LOGIN_REQUEST, NEW_USER_REGISTER, USER_DATA} from './type';
import { json } from 'express';

export const loginRequest = (email, password) => dispatch => {
    let data = {
        email: email,
        password:password,
    }
    fetch('http://localhost:5000/api/user/login', {
        method: 'POST',
        body: JSON.stringify(data),
        headers: {
            "Content-type": "application/json; charset=UTF-8"
        }
    })
        .then(response => response.json())
         .then(json => dispatch({
            type: LOGIN_REQUEST,
            payload: json
        }))
}



export const userDataRequest = () => dispatch => {

    let tokenVal = localStorage.getItem("token") || undefined;
    console.log(tokenVal)
    
    // const data = {
    //     token : tokenVal
    // };
     
    fetch('http://localhost:5000/api/user/userProfile', {
        method: 'POST',
        // body: JSON.stringify(tokenVal),
        headers: {
            "Content-type": "application/json; charset=UTF-8"
        }
    })
        .then(response => response.json())
         .then(json => dispatch({
            type: USER_DATA,
            payload: json
        }))
}
